import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ProductDetails.css'; 

const ProductDetails = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [error, setError] = useState('');


    const productImageMap = {
        '1': 'product1.jpg',
        '2': 'product2.jpg',
        '3': 'product3.jpg',
        '4': 'product4.jpg',
        '5': 'product5.jpg',
        '6': 'product6.jpg',
        '7': 'product7.jpg',
        '8': 'product8.jpg',
        '9': 'product9.jpg',
        '10': 'product10.jpg'
    };

    const productDetailsMap = {
        '1': {
            operatingSystem: 'N/A',
            specialFeature: 'N/A',
            connectivity: 'N/A',
            wireless: 'N/A',
            shape: 'N/A',
            description: 'N/A'
        },
        '2': {
            operatingSystem: 'iOS 15',
            specialFeature: 'Face ID',
            connectivity: 'Wi-Fi 6, Lightning',
            wireless: '5G',
            shape: 'Square',
            description: 'The latest iPhone with cutting-edge technology and facial recognition.'
        },
        '3': {
            operatingSystem: 'Android 11',
            specialFeature: 'Dual Camera',
            connectivity: 'Bluetooth 5.0, Wi-Fi 5',
            wireless: '4G LTE',
            shape: 'Oval',
            description: 'A versatile smartphone with a dual-camera setup and good performance.'
        },
        '4': {
            operatingSystem: 'iOS 14',
            specialFeature: 'MagSafe Charging',
            connectivity: 'Wi-Fi 6, Lightning',
            wireless: '5G',
            shape: 'Square',
            description: 'An iPhone with MagSafe charging and improved battery life.'
        },
        '5': {
            operatingSystem: 'Android 10',
            specialFeature: 'Fast Charging',
            connectivity: 'Bluetooth 4.2, Wi-Fi 5',
            wireless: '4G LTE',
            shape: 'Rectangle',
            description: 'A budget-friendly Android phone with fast charging capabilities.'
        },
        '6': {
            operatingSystem: 'iOS 13',
            specialFeature: 'Waterproof',
            connectivity: 'Wi-Fi 6, Lightning',
            wireless: '4G LTE',
            shape: 'Round',
            description: 'An iPhone with waterproof features and great overall performance.'
        },
        '7': {
            operatingSystem: 'Android 9',
            specialFeature: 'Expandable Storage',
            connectivity: 'Bluetooth 4.1, Wi-Fi 4',
            wireless: '3G',
            shape: 'Rectangle',
            description: 'An Android device with expandable storage and reliable connectivity.'
        },
        '8': {
            operatingSystem: 'iOS 12',
            specialFeature: 'Portrait Mode',
            connectivity: 'Wi-Fi 5, Lightning',
            wireless: '4G LTE',
            shape: 'Square',
            description: 'An iPhone with enhanced portrait mode for better photography.'
        },
        '9': {
            operatingSystem: 'Android 8',
            specialFeature: 'Long Battery Life',
            connectivity: 'Bluetooth 4.0, Wi-Fi 4',
            wireless: '4G LTE',
            shape: 'Rectangle',
            description: 'A durable phone with long battery life and decent performance.'
        },
        '10': {
            operatingSystem: 'iOS 11',
            specialFeature: 'Wireless Charging',
            connectivity: 'Wi-Fi 4, Lightning',
            wireless: '4G LTE',
            shape: 'Round',
            description: 'An older iPhone model with wireless charging support.'
        }
    };

    useEffect(() => {
        const fetchProductDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:4567/products/${id}`);
                const productData = response.data;

                productData.image = productImageMap[id] || 'default.jpg';
                const additionalDetails = productDetailsMap[id] || {};
                setProduct({ ...productData, ...additionalDetails });
            } catch (error) {
                setError('Error fetching product details');
                console.error('Error fetching product details:', error);
            }
        };


        fetchProductDetails();
    }, [id]);

    if (error) {
        return <p style={{ color: 'red' }}>{error}</p>;
    }

    if (!product) {
        return <p>Loading product details...</p>;
    }

    const imagePath = `/images/${product.image}`;

    const stockStatus = product.quantity > 0 ? 'In Stock' : 'Out of Stock';
    const stockStatusClass = product.quantity > 0 ? 'in-stock' : 'out-of-stock';

    return (
        <div className="product-details-container">
            <div className="product-image">
                <img src={imagePath} alt={product.name} />
            </div>
            <div className="product-info">
                <h1>{product.name}</h1>
                <p className="price">â‚¹{product.price}</p>
                <ul className="product-features">
                    <li><strong>Operating System:</strong> {product.operatingSystem || 'N/A'}</li>
                    <li><strong>Special Feature:</strong> {product.specialFeature || 'N/A'}</li>
                    <li><strong>Connectivity Technology:</strong> {product.connectivity || 'N/A'}</li>
                    <li><strong>Wireless Communication:</strong> {product.wireless || 'N/A'}</li>
                    <li><strong>Shape:</strong> {product.shape || 'N/A'}</li>
                    <li><strong>Description:</strong> {product.description}</li>
                </ul>
                <div className={`stock-status ${stockStatusClass}`}>{stockStatus}</div>
            </div>
        </div>
    );
};

export default ProductDetails;
