import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Login from './login'; // Import the Login component
import InventoryTracking from './inventoryTracking'; // Import your InventoryTracking component
import AddProduct from './AddProduct'; // Import the AddProduct component
import UpdateProduct from './UpdateProduct'; // Import the UpdateProduct component

function App() {
    const navigate = useNavigate();

    useEffect(() => {
        navigate('/login'); // Redirect to login on initial load
    }, []);

    return (
        <Router>
            <div className="app-container">
                <Routes>
                    <Route path="/login" element={<Login />} />
                    <Route path="/inventory" element={<InventoryTracking />} />
                    <Route path="/add-product" element={<AddProduct />} />
                    <Route path="/update-product" element={<UpdateProduct />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
