import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import InventoryTracking from './inventoryTracking'; 
import AddProduct from './AddProduct';
import UpdateProduct from './UpdateProduct';
import Login from './login'; 

function App() {
    return (
        <Router>
            <div className="app-container">
                <nav>
                    <ul>
                        <li><Link to="/inventory">Inventory Tracking</Link></li>
                        <li><Link to="/add-product">Add Product</Link></li>
                        <li><Link to="/update-product">Update Product</Link></li>
                        <li><Link to="/login">Login</Link></li> {/* Add login link */}
                    </ul>
                </nav>
                <Routes>
                    <Route path="/inventory" element={<InventoryTracking />} />
                    <Route path="/add-product" element={<AddProduct />} />
                    <Route path="/update-product" element={<UpdateProduct />} />
                    <Route path="/login" element={<Login />} /> {/* Add route for login */}
                    <Route path="/" element={<Login />} /> {/* Default route to login */}
                </Routes>
            </div>
        </Router>
    );
}

export default App;
