import React, { useState } from 'react';
import { addProduct } from './api';
import './AddProduct.css'; 

const AddProduct = () => {
    const [product, setProduct] = useState({
        productId: '',
        name: '',
        description: '',
        category: '',
        price: '',
        quantity: '',
        lifecycleStage: '',
        supplierId: ''
    });

    const [errors, setErrors] = useState({
        productId: '',
        price: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;

        // Validation logic for Product ID and Price
        if (name === 'productId' && value.length > 6) {
            setErrors((prevErrors) => ({
                ...prevErrors,
                productId: 'Product ID should not be more than 6 characters.'
            }));
        } else {
            setErrors((prevErrors) => ({
                ...prevErrors,
                productId: ''
            }));
        }

        if (name === 'price') {
            const priceRegex = /^\d+(\.\d{1,2})?$/; // Regex to match decimal values
            if (!priceRegex.test(value) && value !== '') {
                setErrors((prevErrors) => ({
                    ...prevErrors,
                    price: 'Price must be a valid decimal number.'
                }));
            } else {
                setErrors((prevErrors) => ({
                    ...prevErrors,
                    price: ''
                }));
            }
        }

        setProduct({ ...product, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Final check before submission
        if (errors.productId || errors.price) {
            alert('Please fix the errors before submitting.');
            return;
        }

        try {
            await addProduct(product);
            // Reset form fields after successful product addition
            setProduct({
                productId: '',
                name: '',
                description: '',
                category: '',
                price: '',
                quantity: '',
                lifecycleStage: '',
                supplierId: ''
            });
            alert('Product added successfully!');
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    return (
        <div className="add-product-container">
            <h1 className="title">Add Product</h1>
            <form className="add-product-form" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Product ID</label>
                    <input
                        type="text"
                        name="productId"
                        value={product.productId}
                        onChange={handleChange}
                        placeholder="Product ID"
                        required
                    />
                    {errors.productId && <p className="error-message">{errors.productId}</p>}
                </div>
                <div className="form-group">
                    <label>Name</label>
                    <input
                        type="text"
                        name="name"
                        value={product.name}
                        onChange={handleChange}
                        placeholder="Name"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Description</label>
                    <textarea
                        name="description"
                        value={product.description}
                        onChange={handleChange}
                        placeholder="Description"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Category</label>
                    <input
                        type="text"
                        name="category"
                        value={product.category}
                        onChange={handleChange}
                        placeholder="Category"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input
                        type="text"
                        name="price"
                        value={product.price}
                        onChange={handleChange}
                        placeholder="Price"
                        required
                    />
                    {errors.price && <p className="error-message">{errors.price}</p>}
                </div>
                <div className="form-group">
                    <label>Quantity</label>
                    <input
                        type="number"
                        name="quantity"
                        value={product.quantity}
                        onChange={handleChange}
                        placeholder="Quantity"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Lifecycle Stage</label>
                    <input
                        type="text"
                        name="lifecycleStage"
                        value={product.lifecycleStage}
                        onChange={handleChange}
                        placeholder="Lifecycle Stage"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Supplier ID</label>
                    <input
                        type="number"
                        name="supplierId"
                        value={product.supplierId}
                        onChange={handleChange}
                        placeholder="Supplier ID"
                        required
                    />
                </div>
                <div className="form-group">
                    <button type="submit" className="add-product-button" disabled={errors.productId || errors.price}>
                        Add Product
                    </button>
                </div>
            </form>
        </div>
    );
};

export default AddProduct;
