import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './register.css';
import { registerUser } from './api';

const Register = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        password: ''
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:4567/register', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(formData)
        })
        .then(response => {
            if (response.ok) {
                navigate('/login');
            } else {
                console.log('Registration failed');
            }
        })
        .catch(error => console.log('Error:', error));
    };    

    return (
        <div className="register-page">
            <div className="register-container">
                <h2>Register</h2>
                <p className="register-subtitle">Manage all your inventory efficiently</p>
                <p>Letâ€™s get you all set up so you can verify your personal account and begin setting up your work profile</p>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <input
                            type="text"
                            name="firstName"
                            placeholder="Enter your name"
                            value={formData.firstName}
                            onChange={handleChange}
                            className="register-input"
                        />
                        <input
                            type="text"
                            name="lastName"
                            placeholder="minimum 8 characters"
                            value={formData.lastName}
                            onChange={handleChange}
                            className="register-input"
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="email"
                            name="email"
                            placeholder="Enter your email"
                            value={formData.email}
                            onChange={handleChange}
                            className="register-input"
                        />
                        <input
                            type="text"
                            name="phone"
                            placeholder="minimum 8 characters"
                            value={formData.phone}
                            onChange={handleChange}
                            className="register-input"
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            name="password"
                            placeholder="Enter your password"
                            value={formData.password}
                            onChange={handleChange}
                            className="register-input"
                        />
                    </div>
                    <div className="form-group">
                        <label>
                            <input type="checkbox" /> I agree to all terms, privacy policies, and fees
                        </label>
                    </div>
                    <button type="submit" className="register-button">Sign up</button>
                </form>
                <p className="login-link">Already have an account? <a href="#" onClick={() => navigate('/login')}>Log in</a></p>
            </div>
        </div>
    );
};

export default Register;
