// src/UpdateProduct.js
import React, { useState } from 'react';
import { updateProduct } from './api'; // Adjust path if needed

const UpdateProduct = () => {
    const [productId, setProductId] = useState('');
    const [product, setProduct] = useState({
        name: '',
        description: '',
        category: '',
        price: 0,
        quantity: 0,
        lifecycleStage: '',
        supplierId: 0
    });

    const handleChange = (e) => {
        setProduct({ ...product, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await updateProduct(productId, product);
        // Handle post-submit actions (e.g., showing a success message)
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="productId" onChange={(e) => setProductId(e.target.value)} />
            {/* Form fields for product properties */}
            <button type="submit">Update Product</button>
        </form>
    );
};

export default UpdateProduct;
