// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import './App.css';
import InventoryTracking from './inventoryTracking';
import AddProduct from './AddProduct';
import UpdateProduct from './UpdateProduct';

function App() {
    return (
        <Router>
            <div className="app-container">
                <nav>
                    <ul>
                        <li><Link to="/inventory">Inventory Tracking</Link></li>
                        <li><Link to="/add-product">Add Product</Link></li>
                        <li><Link to="/update-product">Update Product</Link></li>
                    </ul>
                </nav>
                <Switch>
                    <Route path="/inventory" component={InventoryTracking} />
                    <Route path="/add-product" component={AddProduct} />
                    <Route path="/update-product" component={UpdateProduct} />
                    <Route path="/" exact component={InventoryTracking} />
                </Switch>
            </div>
        </Router>
    );
}

export default App;
