import React, { useState, useEffect } from 'react';
import { updateProduct } from './api';
import { useParams } from 'react-router-dom';

const UpdateProduct = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState({
        productId: productId || '',
        name: '',
        description: '',
        category: '',
        price: 0.0,
        quantity: 0,
        lifecycleStage: '',
        supplierId: 0
    });

    useEffect(() => {
        if (productId) {
            setProduct(prevProduct => ({ ...prevProduct, productId }));
        }
    }, [productId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        
        // Handle numeric values
        const updatedValue = name === 'price' || name === 'quantity' || name === 'supplierId' ? 
            (value === '' ? 0 : parseFloat(value)) : value;
        
        setProduct({ ...product, [name]: updatedValue });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateProduct(productId, product);
            setProduct({
                productId: productId || '',
                name: '',
                description: '',
                category: '',
                price: 0.0,
                quantity: 0,
                lifecycleStage: '',
                supplierId: 0
            });
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };

    return (
        <div>
            <h1>Update Product</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="productId"
                    value={product.productId || ''}
                    onChange={handleChange}
                    placeholder="Product ID"
                    required
                />
                <input
                    type="text"
                    name="name"
                    value={product.name || ''}
                    onChange={handleChange}
                    placeholder="Name"
                    required
                />
                <input
                    type="text"
                    name="description"
                    value={product.description || ''}
                    onChange={handleChange}
                    placeholder="Description"
                    required
                />
                <input
                    type="text"
                    name="category"
                    value={product.category || ''}
                    onChange={handleChange}
                    placeholder="Category"
                    required
                />
                <input
                    type="number"
                    name="price"
                    value={product.price}
                    onChange={handleChange}
                    placeholder="Price"
                    required
                />
                <input
                    type="number"
                    name="quantity"
                    value={product.quantity}
                    onChange={handleChange}
                    placeholder="Quantity"
                    required
                />
                <input
                    type="text"
                    name="lifecycleStage"
                    value={product.lifecycleStage || ''}
                    onChange={handleChange}
                    placeholder="Lifecycle Stage"
                    required
                />
                <input
                    type="number"
                    name="supplierId"
                    value={product.supplierId}
                    onChange={handleChange}
                    placeholder="Supplier ID"
                    required
                />
                <button type="submit">Update Product</button>
            </form>
        </div>
    );
};

export default UpdateProduct;
